    display: grid;
    grid-template-columns: 1fr auto 1fr;
    gap: 1px;
    background: var(--weather-secondary);
  }

  .option-row {
    display: contents;
  }

  .option-cell {
    background: var(--weather-card);
    padding: 12px;
    border-bottom: 1px solid var(--weather-secondary);
  }

  .strike-cell {
    background: var(--weather-secondary);
    text-align: center;
    font-weight: 600;
    color: var(--foreground);
  }

  .pulsing {
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  .countdown {
    background: linear-gradient(45deg, var(--weather-warning), var(--weather-danger));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    font-weight: 700;
  }

  .weather-card {
    background: var(--weather-card);
    border: 1px solid var(--border);
    border-radius: var(--radius);
    padding: 1.5rem;
  }

  .weather-button-primary {
    background: var(--weather-primary);
    color: var(--primary-foreground);
    border: none;
    padding: 0.5rem 1rem;
    border-radius: var(--radius);
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s;
  }

  .weather-button-primary:hover {
    background: hsl(218, 100%, 60%);
  }

  .weather-button-danger {
    background: var(--weather-danger);
    color: white;
    border: none;
    padding: 0.5rem 1rem;
    border-radius: var(--radius);
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s;
  }

  .weather-button-danger:hover {
    background: hsl(0, 72%, 46%);
  }

  .weather-input {
    background: var(--weather-secondary);
    border: 1px solid var(--border);
    border-radius: var(--radius);
    padding: 0.5rem 1rem;
    color: var(--foreground);
    width: 100%;
  }

  .weather-input:focus {
    outline: none;
    border-color: var(--weather-primary);
    box-shadow: 0 0 0 2px hsla(218, 100%, 66%, 0.2);
  }

  .weather-input::placeholder {
    color: var(--weather-muted);
  }

  .weather-badge {
    padding: 0.25rem 0.5rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 500;
  }

  .weather-badge-primary {
    background: hsla(218, 100%, 66%, 0.2);
    color: var(--weather-primary);
  }

  .weather-badge-danger {
    background: hsla(0, 72%, 51%, 0.2);
    color: var(--weather-danger);
  }

  .weather-badge-warning {
    background: hsla(42, 96%, 50%, 0.2);
    color: var(--weather-warning);
  }

  .weather-badge-info {
    background: hsla(218, 100%, 66%, 0.2);
    color: var(--weather-info);
  }

  .weather-badge-ai {
    background: hsla(269, 100%, 72%, 0.2);
    color: var(--weather-ai);
  }

  .weather-tab {
    flex: 1;
    padding: 0.5rem 1rem;
    border-radius: var(--radius);
    font-weight: 500;
    cursor: pointer;
    transition: all 0.2s;
    text-align: center;
  }

  .weather-tab.active {
    background: var(--weather-primary);
    color: var(--primary-foreground);
  }

  .weather-tab:not(.active) {
    color: var(--weather-muted);
  }

  .weather-tab:not(.active):hover {
    color: var(--foreground);
  }

  .weather-tooltip {
    position: absolute;
    z-index: 1000;
    background: var(--weather-card);
    color: var(--foreground);
    padding: 0.5rem 0.75rem;
    border-radius: var(--radius);
    font-size: 0.75rem;
    border: 1px solid var(--weather-primary);
    box-shadow: 0 4px 12px hsla(0, 0%, 0%, 0.3);
  }

  .weather-modal {
    position: fixed;
    inset: 0;
    background: hsla(0, 0%, 0%, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 50;
  }

  .weather-modal-content {
    background: var(--weather-card);
    border-radius: var(--radius);
    padding: 2rem;
    max-width: 32rem;
    width: 90%;
    margin: 1rem;
    border: 1px solid var(--border);
  }

  .weather-progress {
    width: 100%;
    height: 0.5rem;
    background: var(--weather-secondary);
    border-radius: 9999px;
    overflow: hidden;
  }

  .weather-progress-fill {
    height: 100%;
    background: var(--weather-primary);
    transition: width 0.3s ease;
  }

  .weather-stat-card {
    background: var(--weather-card);
    border: 1px solid var(--border);
    border-radius: var(--radius);
    padding: 1.5rem;
    text-align: center;
  }

  .weather-stat-value {
    font-size: 2rem;
    font-weight: 700;
    color: var(--weather-primary);
    margin-bottom: 0.5rem;
  }

  .weather-stat-label {
    font-size: 0.875rem;
    color: var(--weather-muted);
  }

  .weather-activity-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem 0;
    border-bottom: 1px solid var(--border);
  }

  .weather-activity-item:last-child {
    border-bottom: none;
  }

  .weather-activity-icon {
    width: 2rem;
    height: 2rem;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.875rem;
  }

  .weather-activity-content {
    flex: 1;
  }

  .weather-activity-title {
    font-size: 0.875rem;
    color: var(--foreground);
    margin-bottom: 0.25rem;
  }

  .weather-activity-time {
    font-size: 0.75rem;
    color: var(--weather-muted);
  }
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--weather-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--weather-muted);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--weather-primary);
}

/* Focus visible improvements */
*:focus-visible {
  outline: 2px solid var(--weather-primary);
  outline-offset: 2px;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  :root {
    --background: hsl(0, 0%, 0%);
    --foreground: hsl(0, 0%, 100%);
    --border: hsl(0, 0%, 50%);
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

