                        <span className="ml-auto text-muted-foreground">
                          {(weatherData as any)?.dataHash?.slice(0, 8) || "..."}
                        </span>
                      </div>

                      {/* Rainfall Risk Indicators */}
                      {weatherMetric === "rainfall" && (
                        <div className="space-y-2">
                          {/* Drought Risk */}
