                  </CardContent>
                </Card>
              </div>

              {/* Weather Trend Chart */}
              {trendData && trendData.length > 0 ? (
                <WeatherChart 
                  data={trendData}
                  selectedCity={selectedCity}
                  weatherMetric={weatherMetric}
                  timePeriod={timePeriod}
                  onTimePeriodChange={setTimePeriod}
