import React, { useState } from 'react';
import { useMutation, useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Bot, Zap, Clock, CheckCircle, X, ArrowRight } from 'lucide-react';

interface TradeRecommendation {
  contractType: string;
  strategy: string;
  underlying: string;
  location?: string;
  stationId?: string;
  strikePrice?: number;
  premium: number;
  quantity: number;
  expiryDate: string;
  reasoning: string;
  riskAssessment: string;
  entryConditions: string[];
  exitConditions: string[];
  maxLoss: number;
  potentialProfit: number;
  probability: number;
  tradeSetup?: string;
  greeksAnalysis?: string;
}

interface NLPTradeResponse {
  parameters: {
    maxLoss: number;
    duration: number;
    capital: number;
    tradeType: string;
    underlying?: string;
    city?: string;
    strategy?: string;
  };
  recommendations: TradeRecommendation[];
  automatedRules: string[];
  response: string;
}

export function FlowAISetup() {
  const [tradeInput, setTradeInput] = useState("");
  const [acceptedTrades, setAcceptedTrades] = useState<Set<number>>(new Set());

  const processTradeRequest = useMutation({
    mutationFn: async (input: string): Promise<NLPTradeResponse> => {
      return apiRequest('/api/ai/process-trade', {
        method: 'POST',
        body: { query: input }
      });
    },
  });

  const handleAcceptTrade = (trade: TradeRecommendation, index: number) => {
    setAcceptedTrades(new Set([...acceptedTrades, index]));
    console.log('Trade accepted:', trade);
  };

  const handleRejectTrade = (trade: TradeRecommendation, index: number) => {
    console.log('Trade rejected:', trade);
  };

  const handleTradeSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (tradeInput.trim()) {
      processTradeRequest.mutate(tradeInput);
      setTradeInput("");
    }
  };

  const examples = [
    "I want to trade high probability trade, with max loss $1.75 duration 15 days capital to use $5",
    "Conservative rainfall strategy for Dallas, $10 capital, max loss $2, 30 days",
  ];

  return (
    <div className="space-y-3">
      {/* Status Badge */}
      <div className="flex items-center justify-between text-xs">
        <Badge variant="outline" className="text-xs px-2 py-0">
          Natural Language Trading
        </Badge>
        <span className="text-muted-foreground">Active</span>
      </div>

      {/* Compact Input Form */}
      <form onSubmit={handleTradeSubmit} className="space-y-2">
        <Textarea
          placeholder="Describe your trading strategy..."
          value={tradeInput}
          onChange={(e) => setTradeInput(e.target.value)}
          className="min-h-[60px] text-sm resize-none"
        />
        <Button 
          type="submit" 
          disabled={processTradeRequest.isPending || !tradeInput.trim()}
          size="sm"
          className="w-full h-8"
        >
          {processTradeRequest.isPending ? (
            <>
              <Clock className="h-3 w-3 mr-1 animate-spin" />
              Processing...
            </>
          ) : (
            <>
              <Zap className="h-3 w-3 mr-1" />
              Analyze Trade
            </>
          )}
        </Button>
      </form>

      {/* Quick Examples */}
      <div>
        <h4 className="text-xs font-medium mb-1 text-muted-foreground">Quick Examples:</h4>
        <div className="space-y-1">
          {examples.map((example, index) => (
            <Button
              key={index}
              variant="ghost"
              size="sm"
              className="w-full text-left h-auto py-1 px-2 justify-start text-xs hover:bg-muted/50"
              onClick={() => setTradeInput(example)}
            >
              <ArrowRight className="h-2 w-2 mr-1 flex-shrink-0" />
              <span className="truncate">{example.substring(0, 40)}...</span>
            </Button>
          ))}
        </div>
      </div>

      {/* AI Response - Compact */}
      {processTradeRequest.data && (
        <div className="mt-3 p-2 bg-muted/50 rounded-md">
          <div className="flex items-center gap-1 mb-2">
            <Bot className="h-3 w-3 text-primary" />
            <span className="text-xs font-medium">AI Analysis</span>
          </div>

          <div className="space-y-2">
            <div className="text-xs">
              <div className="grid grid-cols-2 gap-1 text-muted-foreground">
                <div>Max Loss: ${processTradeRequest.data.parameters.maxLoss}</div>
                <div>Days: {processTradeRequest.data.parameters.duration}</div>
                <div>Capital: ${processTradeRequest.data.parameters.capital}</div>
                <div>Type: {processTradeRequest.data.parameters.tradeType}</div>
              </div>
            </div>

            {processTradeRequest.data.recommendations && processTradeRequest.data.recommendations.length > 0 && (
              <div className="space-y-2">
                <h5 className="text-xs font-medium">Recommendations:</h5>
                {processTradeRequest.data.recommendations.slice(0, 2).map((rec: TradeRecommendation, index: number) => (
                  <div key={index} className="border rounded p-2 bg-background/50">
                    <div className="flex items-center justify-between mb-1">
                      <Badge variant="outline" className="text-xs px-1 py-0">
                        {rec.strategy.replace('_', ' ').toUpperCase()}
                      </Badge>
                      <div className="flex gap-1">
                        <Button
                          size="sm"
                          onClick={() => handleAcceptTrade(rec, index)}
                          className="h-5 px-2 text-xs bg-green-600 hover:bg-green-700"
                        >
                          <CheckCircle className="h-2 w-2" />
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleRejectTrade(rec, index)}
                          className="h-5 px-2 text-xs"
                        >
                          <X className="h-2 w-2" />
                        </Button>
                      </div>
                    </div>

                    <div className="text-xs space-y-0.5 text-muted-foreground">
                      <div>{rec.location} â€¢ ${rec.premium}</div>
                      <div>Max Loss: ${rec.maxLoss} â€¢ Profit: ${rec.potentialProfit}</div>
                      <div className="text-xs mt-1 truncate">{rec.reasoning}</div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/community/governan.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Vote, Clock, CheckCircle, XCircle, Users, FileText } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface GovernanceProposal {
  id: number;
  proposalId: string;
  poolId: string;
  title: string;
  description: string;
  proposalType: string;
  proposedValue: string;
  votesFor: number;
  votesAgainst: number;
  totalVotes: number;
  status: string;
  createdAt: string;
  expiresAt: string;
}

export default function Governance() {
  const [selectedProposal, setSelectedProposal] = useState<string | null>(null);
  const [selectedPoolId] = useState("dallas_drought_relief"); // Default pool
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: proposals, isLoading } = useQuery({
    queryKey: ["/api/governance/proposals", selectedPoolId],
    refetchInterval: 30000
  });

  const { data: pools } = useQuery({
    queryKey: ["/api/pools"],
  });

  const voteMutation = useMutation({
    mutationFn: async ({ proposalId, vote }: { proposalId: string; vote: "for" | "against" }) => {
      return apiRequest("POST", "/api/governance/vote", {
        proposalId,
        userId: 1, // Mock user ID
        vote,
        votingPower: 100 // Mock voting power based on stake
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/governance/proposals"] });
      toast({
        title: "Vote Cast",
        description: "Your vote has been recorded successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to cast vote. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleVote = (proposalId: string, vote: "for" | "against") => {
    voteMutation.mutate({ proposalId, vote });
  };

  const getTimeRemaining = (expiresAt: string) => {
    const now = new Date();
    const expiry = new Date(expiresAt);
    const diff = expiry.getTime() - now.getTime();

    if (diff <= 0) return "Expired";

    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

    return `${days}d ${hours}h remaining`;
  };

  const getVotePercentage = (votesFor: number, totalVotes: number) => {
    if (totalVotes === 0) return 0;
    return (votesFor / totalVotes) * 100;
  };

  const getProposalStatusColor = (status: string) => {
    switch (status) {
      case "active": return "bg-chart-3/20 text-chart-3";
      case "passed": return "bg-primary/20 text-primary";
      case "failed": return "bg-chart-4/20 text-chart-4";
      default: return "bg-secondary/20 text-secondary-foreground";
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Governance & Proposals</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Governance Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="pt-6 text-center">
            <div className="text-2xl font-bold text-primary mb-2">
              {proposals?.filter((p: GovernanceProposal) => p.status === "active").length || 0}
            </div>
            <div className="text-sm text-muted-foreground">Active Proposals</div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6 text-center">
            <div className="text-2xl font-bold text-chart-2 mb-2">
              {proposals?.reduce((sum: number, p: GovernanceProposal) => sum + p.totalVotes, 0) || 0}
            </div>
            <div className="text-sm text-muted-foreground">Total Votes Cast</div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6 text-center">
            <div className="text-2xl font-bold text-chart-3 mb-2">
              {proposals?.filter((p: GovernanceProposal) => p.status === "passed").length || 0}
            </div>
            <div className="text-sm text-muted-foreground">Proposals Passed</div>
          </CardContent>
        </Card>
      </div>

      {/* Active Proposals */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Vote className="h-5 w-5" />
            Active Proposals
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {proposals?.filter((proposal: GovernanceProposal) => proposal.status === "active").map((proposal: GovernanceProposal) => {
              const yesPercentage = getVotePercentage(proposal.votesFor, proposal.totalVotes);
              const noPercentage = getVotePercentage(proposal.votesAgainst, proposal.totalVotes);

              return (
                <div key={proposal.proposalId} className="border border-border rounded-lg p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <h4 className="font-medium">{proposal.title}</h4>
                        <Badge className={getProposalStatusColor(proposal.status)}>
                          {proposal.status}
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-2">{proposal.description}</p>
                      <div className="flex items-center gap-4 text-xs text-muted-foreground">
                        <span className="flex items-center gap-1">
                          <Clock className="h-3 w-3" />
                          {getTimeRemaining(proposal.expiresAt)}
                        </span>
                        <span className="flex items-center gap-1">
                          <Users className="h-3 w-3" />
                          {proposal.totalVotes} votes
                        </span>
                        <span className="flex items-center gap-1">
                          <FileText className="h-3 w-3" />
                          {proposal.proposalType.replace("_", " ")}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Vote Progress */}
                  <div className="space-y-3">
                    <div className="flex justify-between text-sm mb-2">
                      <span className="flex items-center gap-1">
                        <CheckCircle className="h-4 w-4 text-primary" />
                        Yes: {yesPercentage.toFixed(1)}% ({proposal.votesFor})
                      </span>
                      <span className="flex items-center gap-1">
                        <XCircle className="h-4 w-4 text-chart-4" />
                        No: {noPercentage.toFixed(1)}% ({proposal.votesAgainst})
                      </span>
                    </div>

                    <div className="relative">
                      <Progress value={yesPercentage} className="h-3" />
                      <div 
                        className="absolute top-0 right-0 h-3 bg-chart-4 rounded-r-full" 
                        style={{ width: `${noPercentage}%` }}
                      />
                    </div>

                    <div className="flex gap-2 pt-2">
                      <Button
                        size="sm"
                        onClick={() => handleVote(proposal.proposalId, "for")}
                        disabled={voteMutation.isPending}
                        className="bg-primary hover:bg-primary/80 flex-1"
                      >
                        <CheckCircle className="h-4 w-4 mr-1" />
                        Vote Yes
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleVote(proposal.proposalId, "against")}
                        disabled={voteMutation.isPending}
                        className="border-chart-4 text-chart-4 hover:bg-chart-4/10 flex-1"
                      >
                        <XCircle className="h-4 w-4 mr-1" />
                        Vote No
                      </Button>
                    </div>
                  </div>
                </div>
              );
            })}

            {(!proposals || proposals.filter((p: GovernanceProposal) => p.status === "active").length === 0) && (
              <div className="text-center py-8 text-muted-foreground">
                <Vote className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>No active proposals at this time.</p>
                <p className="text-sm">Check back later or create a new proposal.</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Recent Governance Activity */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Governance Activity</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            <div className="flex items-center justify-between p-3 bg-secondary/30 rounded">
              <div>
                <div className="font-medium text-sm">Proposal "Reduce payout threshold" created</div>
                <div className="text-xs text-muted-foreground">Dallas Drought Relief Pool â€¢ 2 hours ago</div>
              </div>
              <Badge className="bg-chart-3/20 text-chart-3">New</Badge>
            </div>

            <div className="flex items-center justify-between p-3 bg-secondary/30 rounded">
              <div>
                <div className="font-medium text-sm">Proposal "Increase pool cap" passed</div>
                <div className="text-xs text-muted-foreground">Texas Flood Protection Pool â€¢ 1 day ago</div>
              </div>
              <Badge className="bg-primary/20 text-primary">Passed</Badge>
            </div>

            <div className="flex items-center justify-between p-3 bg-secondary/30 rounded">
              <div>
                <div className="font-medium text-sm">127 votes cast on threshold proposal</div>
                <div className="text-xs text-muted-foreground">Dallas Drought Relief Pool â€¢ 2 days ago</div>
              </div>
              <Badge variant="outline">Voting</Badge>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

