interface FlareMarketData {
  currentWindSpeed: number;
  flrUsdPrice: number;
  lastUpdate: number;
}

interface WindFuture {
  contractId: string;
  trader: string;
  isLong: boolean;
  strikePrice: number;
  notionalAmount: number;
  collateralAmount: number;
