                  <div>
                    <Label className="text-sm font-medium">Contract Registry</Label>
                    <div className="text-sm text-muted-foreground font-mono bg-muted p-2 rounded flex items-center justify-between">
                      {deploymentInfo.contractRegistry}
                      <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                        <ExternalLink className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>

                  {deploymentInfo.contractAddress && (
                    <div>
                      <Label className="text-sm font-medium">Wind Futures Contract</Label>
                      <div className="text-sm text-muted-foreground font-mono bg-muted p-2 rounded flex items-center justify-between">
                        {deploymentInfo.contractAddress}
                        <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                          <ExternalLink className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
              )}

              <Separator />

              <div className="space-y-2">
                <h4 className="text-sm font-medium">Features</h4>
                <ul className="text-sm text-muted-foreground space-y-1">
                  <li>â€¢ Automatic settlement using Flare FTSO price feeds</li>
                  <li>â€¢ FLR and USDT collateral support</li>
                  <li>â€¢ 20% margin requirement</li>
                  <li>â€¢ 7, 14, and 30-day expiry options</li>
                  <li>â€¢ Strike prices from 4-24 mph in 1 mph increments</li>
                  <li>â€¢ Gas-free transactions on Flare Network</li>
                </ul>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

