contracts/CommunityStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title CommunityStaking
 * @dev Community staking contract for Flow and Flare tokens with yield farming
 */
contract CommunityStaking is ReentrancyGuard, Ownable, Pausable {
    struct StakingPool {
        IERC20 stakingToken;
        IERC20 rewardToken;
        uint256 rewardRate; // Rewards per second
        uint256 lastUpdateTime;
        uint256 rewardPerTokenStored;
        uint256 totalStaked;
        uint256 lockPeriod; // Minimum lock time in seconds
        bool active;
    }

    struct UserStake {
        uint256 balance;
        uint256 userRewardPerTokenPaid;
        uint256 rewards;
        uint256 stakeTime;
        uint256 lockEndTime;
    }

    mapping(uint256 => StakingPool) public stakingPools;
    mapping(uint256 => mapping(address => UserStake)) public userStakes;
    mapping(address => uint256[]) public userPoolIds;

    uint256 public poolCount;
    uint256 public constant REWARD_DURATION = 7 days;
    uint256 public constant MIN_STAKE_AMOUNT = 1e18; // 1 token minimum

    event PoolCreated(uint256 indexed poolId, address stakingToken, address rewardToken, uint256 rewardRate);
    event Staked(address indexed user, uint256 indexed poolId, uint256 amount);
    event Withdrawn(address indexed user, uint256 indexed poolId, uint256 amount);
    event RewardPaid(address indexed user, uint256 indexed poolId, uint256 reward);
    event PoolUpdated(uint256 indexed poolId, uint256 newRewardRate);
    event EmergencyWithdraw(address indexed user, uint256 indexed poolId, uint256 amount);

    constructor() Ownable(msg.sender) {}

    modifier updateReward(uint256 poolId, address account) {
        StakingPool storage pool = stakingPools[poolId];
        pool.rewardPerTokenStored = rewardPerToken(poolId);
        pool.lastUpdateTime = lastTimeRewardApplicable(poolId);

        if (account != address(0)) {
            UserStake storage userStake = userStakes[poolId][account];
            userStake.rewards = earned(poolId, account);
            userStake.userRewardPerTokenPaid = pool.rewardPerTokenStored;
        }
        _;
    }

    modifier poolExists(uint256 poolId) {
        require(poolId < poolCount, "Pool does not exist");
        require(stakingPools[poolId].active, "Pool is not active");
        _;
    }

    /**
     * @dev Create a new staking pool
     */
    function createPool(
        address _stakingToken,
        address _rewardToken,
        uint256 _rewardRate,
        uint256 _lockPeriod
    ) external onlyOwner {
        require(_stakingToken != address(0), "Invalid staking token");
        require(_rewardToken != address(0), "Invalid reward token");
        require(_rewardRate > 0, "Reward rate must be positive");

        stakingPools[poolCount] = StakingPool({
            stakingToken: IERC20(_stakingToken),
            rewardToken: IERC20(_rewardToken),
            rewardRate: _rewardRate,
            lastUpdateTime: block.timestamp,
            rewardPerTokenStored: 0,
            totalStaked: 0,
            lockPeriod: _lockPeriod,
            active: true
        });

        emit PoolCreated(poolCount, _stakingToken, _rewardToken, _rewardRate);
        poolCount++;
    }

    /**
     * @dev Stake tokens in a specific pool
     */
    function stake(uint256 poolId, uint256 amount) 
        external 
        nonReentrant 
        whenNotPaused 
        poolExists(poolId) 
        updateReward(poolId, msg.sender) 
    {
        require(amount >= MIN_STAKE_AMOUNT, "Amount below minimum");

        StakingPool storage pool = stakingPools[poolId];
        UserStake storage userStake = userStakes[poolId][msg.sender];

        // Transfer tokens to contract
        pool.stakingToken.transferFrom(msg.sender, address(this), amount);

        // Update state
        if (userStake.balance == 0) {
            userPoolIds[msg.sender].push(poolId);
        }

        userStake.balance += amount;
        userStake.stakeTime = block.timestamp;
        userStake.lockEndTime = block.timestamp + pool.lockPeriod;
        pool.totalStaked += amount;

        emit Staked(msg.sender, poolId, amount);
    }

    /**
     * @dev Withdraw staked tokens and claim rewards
     */
    function withdraw(uint256 poolId, uint256 amount) 
        external 
        nonReentrant 
        poolExists(poolId) 
        updateReward(poolId, msg.sender) 
    {
        UserStake storage userStake = userStakes[poolId][msg.sender];
        require(userStake.balance >= amount, "Insufficient balance");
        require(block.timestamp >= userStake.lockEndTime, "Tokens still locked");

        StakingPool storage pool = stakingPools[poolId];

        userStake.balance -= amount;
        pool.totalStaked -= amount;

        // Transfer tokens back to user
        pool.stakingToken.transfer(msg.sender, amount);

        emit Withdrawn(msg.sender, poolId, amount);
    }

    /**
     * @dev Claim earned rewards
     */
    function claimReward(uint256 poolId) 
        external 
        nonReentrant 
        poolExists(poolId) 
        updateReward(poolId, msg.sender) 
    {
        UserStake storage userStake = userStakes[poolId][msg.sender];
        uint256 reward = userStake.rewards;

        if (reward > 0) {
            userStake.rewards = 0;
            StakingPool storage pool = stakingPools[poolId];
            pool.rewardToken.transfer(msg.sender, reward);
            emit RewardPaid(msg.sender, poolId, reward);
        }
    }

    /**
     * @dev Emergency withdraw without rewards (in case of emergency)
     */
    function emergencyWithdraw(uint256 poolId) 
        external 
        nonReentrant 
        poolExists(poolId) 
    {
        UserStake storage userStake = userStakes[poolId][msg.sender];
        uint256 amount = userStake.balance;
        require(amount > 0, "No balance to withdraw");

        StakingPool storage pool = stakingPools[poolId];

        userStake.balance = 0;
        userStake.rewards = 0;
        pool.totalStaked -= amount;

        pool.stakingToken.transfer(msg.sender, amount);
        emit EmergencyWithdraw(msg.sender, poolId, amount);
    }

    /**
     * @dev Calculate reward per token
     */
    function rewardPerToken(uint256 poolId) public view returns (uint256) {
        StakingPool memory pool = stakingPools[poolId];
        if (pool.totalStaked == 0) {
            return pool.rewardPerTokenStored;
        }
        return
            pool.rewardPerTokenStored +
            (((lastTimeRewardApplicable(poolId) - pool.lastUpdateTime) * pool.rewardRate * 1e18) / pool.totalStaked);
    }

    /**
     * @dev Calculate earned rewards for a user
     */
    function earned(uint256 poolId, address account) public view returns (uint256) {
        UserStake memory userStake = userStakes[poolId][account];
        return
            ((userStake.balance * (rewardPerToken(poolId) - userStake.userRewardPerTokenPaid)) / 1e18) +
            userStake.rewards;
    }

    /**
     * @dev Get last applicable reward time
     */
    function lastTimeRewardApplicable(uint256 poolId) public view returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev Get user's staking info for all pools
     */
    function getUserStakes(address user) external view returns (
        uint256[] memory poolIds,
        uint256[] memory balances,
        uint256[] memory rewards,
        uint256[] memory lockEndTimes
    ) {
        uint256[] memory userPools = userPoolIds[user];
        uint256 activeStakes = 0;

        // Count active stakes
        for (uint256 i = 0; i < userPools.length; i++) {
            if (userStakes[userPools[i]][user].balance > 0) {
                activeStakes++;
            }
        }

        poolIds = new uint256[](activeStakes);
        balances = new uint256[](activeStakes);
        rewards = new uint256[](activeStakes);
        lockEndTimes = new uint256[](activeStakes);

        uint256 index = 0;
        for (uint256 i = 0; i < userPools.length; i++) {
            uint256 poolId = userPools[i];
            UserStake memory userStake = userStakes[poolId][user];

            if (userStake.balance > 0) {
                poolIds[index] = poolId;
                balances[index] = userStake.balance;
                rewards[index] = earned(poolId, user);
                lockEndTimes[index] = userStake.lockEndTime;
                index++;
            }
        }
    }

    /**
     * @dev Get pool information
     */
    function getPoolInfo(uint256 poolId) external view returns (
        address stakingToken,
        address rewardToken,
        uint256 rewardRate,
        uint256 totalStaked,
        uint256 lockPeriod,
        bool active
    ) {
        StakingPool memory pool = stakingPools[poolId];
        return (
            address(pool.stakingToken),
            address(pool.rewardToken),
            pool.rewardRate,
            pool.totalStaked,
            pool.lockPeriod,
            pool.active
        );
    }

    /**
     * @dev Update reward rate for a pool (owner only)
     */
    function updateRewardRate(uint256 poolId, uint256 newRewardRate) 
        external 
        onlyOwner 
        poolExists(poolId) 
        updateReward(poolId, address(0)) 
    {
        stakingPools[poolId].rewardRate = newRewardRate;
        emit PoolUpdated(poolId, newRewardRate);
    }

    /**
     * @dev Pause/unpause contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Deactivate a pool
     */
    function deactivatePool(uint256 poolId) external onlyOwner poolExists(poolId) {
        stakingPools[poolId].active = false;
    }

    /**
     * @dev Get APY for a pool (approximate)
     */
    function getPoolAPY(uint256 poolId) external view returns (uint256) {
        StakingPool memory pool = stakingPools[poolId];
        if (pool.totalStaked == 0) return 0;

        // Annual reward rate as percentage
        uint256 annualRewards = pool.rewardRate * 365 days;
        return (annualRewards * 10000) / pool.totalStaked; // Returns APY in basis points
    }
}

contracts/FlareWindFutures.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title FlareWindFutures
 * @dev Wind futures trading contract on Flare Network with FTSO price feeds
 * Supports long/short positions with automatic settlement and FLR/USDT collateral
 */
// Flare FTSO interfaces
interface IFtsoV2 {
    function getFeedById(bytes21 feedId) external view returns (uint256 value, int8 decimals, uint64 timestamp);
}

interface IContractRegistry {
    function getFtsoV2() external view returns (IFtsoV2);
}

contract FlareWindFutures is ReentrancyGuard, Ownable {

    // Contract state
    IContractRegistry public immutable contractRegistry;
    IFtsoV2 public ftsoV2;

    // Supported tokens
    IERC20 public immutable flrToken;
    IERC20 public immutable usdtToken;

    // Wind data feed ID (will be updated with actual Flare feed ID)
    bytes21 public constant WIND_SPEED_FEED_ID = 0x01574E442f55534400000000000000000000000000; // Placeholder
    bytes21 public constant FLR_USD_FEED_ID = 0x01464c522f55534400000000000000000000000000;

    struct WindFuture {
        bytes32 contractId;
        address trader;
        bool isLong; // true = long, false = short
        uint256 strikePrice; // Wind speed in mph * 100 (e.g., 1350 = 13.5 mph)
        uint256 notionalAmount; // Position size in USD
        uint256 collateralAmount; // Collateral deposited
        address collateralToken; // FLR or USDT
        uint256 expiryTimestamp;
        bool isSettled;
        int256 pnl; // Profit/Loss in USD
        uint256 createdAt;
    }

    struct MarketData {
        uint256 currentWindSpeed; // Wind speed * 100
        uint256 flrUsdPrice; // FLR/USD price
        uint256 lastUpdate;
    }

    // State variables
    mapping(bytes32 => WindFuture) public windFutures;
    mapping(address => bytes32[]) public userPositions;
    bytes32[] public allContracts;

    uint256 public constant MARGIN_REQUIREMENT = 20; // 20% margin requirement
    uint256 public constant SETTLEMENT_WINDOW = 1 hours; // Settlement grace period

    MarketData public marketData;

    // Events
    event FutureCreated(
        bytes32 indexed contractId,
        address indexed trader,
        bool isLong,
        uint256 strikePrice,
        uint256 notionalAmount,
        uint256 collateralAmount,
        address collateralToken,
        uint256 expiryTimestamp
    );

    event FutureSettled(
        bytes32 indexed contractId,
        address indexed trader,
        int256 pnl,
        uint256 windSpeedAtExpiry
    );

    event MarketDataUpdated(
        uint256 windSpeed,
        uint256 flrUsdPrice,
        uint256 timestamp
    );

    constructor(
        address _contractRegistry,
        address _flrToken,
        address _usdtToken
    ) Ownable(msg.sender) {
        contractRegistry = IContractRegistry(_contractRegistry);
        ftsoV2 = contractRegistry.getFtsoV2();
        flrToken = IERC20(_flrToken);
        usdtToken = IERC20(_usdtToken);
    }

    /**
     * @dev Create a new wind future position
     * @param _isLong True for long position, false for short
     * @param _strikePrice Wind speed strike price (mph * 100)
     * @param _notionalAmount Position size in USD
     * @param _collateralToken Address of collateral token (FLR or USDT)
     * @param _expiryDays Number of days until expiry (7, 14, or 30)
     */
    function createWindFuture(
        bool _isLong,
        uint256 _strikePrice,
        uint256 _notionalAmount,
        address _collateralToken,
        uint256 _expiryDays
    ) external nonReentrant {
        require(_expiryDays == 7 || _expiryDays == 14 || _expiryDays == 30, "Invalid expiry period");
        require(_strikePrice >= 400 && _strikePrice <= 2400, "Strike price out of range (4-24 mph)");
        require(_notionalAmount >= 100e18, "Minimum position size: $100");
        require(_collateralToken == address(flrToken) || _collateralToken == address(usdtToken), "Invalid collateral token");

        // Update market data from FTSO
        updateMarketData();

        // Calculate required collateral
        uint256 requiredCollateral = (_notionalAmount * MARGIN_REQUIREMENT) / 100;
        uint256 collateralInToken;

        if (_collateralToken == address(flrToken)) {
            // Convert USD to FLR using FTSO price
            collateralInToken = (requiredCollateral * 1e18) / marketData.flrUsdPrice;
        } else {
            // USDT collateral (assuming 1:1 USD)
            collateralInToken = requiredCollateral;
        }

        // Transfer collateral
        IERC20(_collateralToken).transferFrom(msg.sender, address(this), collateralInToken);

        // Create contract
        bytes32 contractId = keccak256(abi.encodePacked(
            msg.sender,
            _strikePrice,
            _notionalAmount,
            block.timestamp,
            allContracts.length
        ));

        uint256 expiryTimestamp = block.timestamp + (_expiryDays * 1 days);

        windFutures[contractId] = WindFuture({
            contractId: contractId,
            trader: msg.sender,
            isLong: _isLong,
            strikePrice: _strikePrice,
            notionalAmount: _notionalAmount,
            collateralAmount: collateralInToken,
            collateralToken: _collateralToken,
            expiryTimestamp: expiryTimestamp,
            isSettled: false,
            pnl: 0,
            createdAt: block.timestamp
        });

        userPositions[msg.sender].push(contractId);
        allContracts.push(contractId);

        emit FutureCreated(
            contractId,
            msg.sender,
            _isLong,
            _strikePrice,
            _notionalAmount,
            collateralInToken,
            _collateralToken,
            expiryTimestamp
        );
    }

    /**
     * @dev Settle an expired wind future contract
     * @param _contractId Contract ID to settle
     */
    function settleFuture(bytes32 _contractId) external nonReentrant {
        WindFuture storage future = windFutures[_contractId];
        require(future.trader != address(0), "Contract does not exist");
        require(!future.isSettled, "Contract already settled");
        require(block.timestamp >= future.expiryTimestamp, "Contract not yet expired");
        require(block.timestamp <= future.expiryTimestamp + SETTLEMENT_WINDOW, "Settlement window expired");

        // Update market data to get settlement wind speed
        updateMarketData();
        uint256 settlementWindSpeed = marketData.currentWindSpeed;

        // Calculate P&L
        int256 priceDifference = int256(settlementWindSpeed) - int256(future.strikePrice);

        // For long positions: profit if wind speed > strike, loss if wind speed < strike
        // For short positions: profit if wind speed < strike, loss if wind speed > strike
        if (!future.isLong) {
            priceDifference = -priceDifference;
        }

        // P&L = (price difference / strike price) * notional amount
        int256 pnl = (priceDifference * int256(future.notionalAmount)) / int256(future.strikePrice);

        future.pnl = pnl;
        future.isSettled = true;

        // Calculate payout
        uint256 totalPayout = future.collateralAmount;

        if (pnl > 0) {
            // Profit - add to collateral
            uint256 profitInToken;
            if (future.collateralToken == address(flrToken)) {
                profitInToken = (uint256(pnl) * 1e18) / marketData.flrUsdPrice;
            } else {
                profitInToken = uint256(pnl);
            }
            totalPayout += profitInToken;
        } else if (pnl < 0) {
            // Loss - deduct from collateral
            uint256 lossInToken;
            if (future.collateralToken == address(flrToken)) {
                lossInToken = (uint256(-pnl) * 1e18) / marketData.flrUsdPrice;
            } else {
                lossInToken = uint256(-pnl);
            }

            if (lossInToken >= totalPayout) {
                totalPayout = 0; // Total loss
            } else {
                totalPayout -= lossInToken;
            }
        }

        // Transfer payout to trader
        if (totalPayout > 0) {
            IERC20(future.collateralToken).transfer(future.trader, totalPayout);
        }

        emit FutureSettled(_contractId, future.trader, pnl, settlementWindSpeed);
    }

    /**
     * @dev Update market data from Flare FTSO
     */
    function updateMarketData() public {
        // Get FLR/USD price from FTSO
        (uint256 flrUsdValue, int8 flrDecimals, uint64 flrTimestamp) = ftsoV2.getFeedById(FLR_USD_FEED_ID);

        // Convert price to 18 decimals
        uint256 flrUsdPrice;
        if (flrDecimals >= 0) {
            flrUsdPrice = flrUsdValue * (10 ** (18 - uint8(flrDecimals)));
        } else {
            flrUsdPrice = flrUsdValue / (10 ** uint8(-flrDecimals));
        }

        // For now, use a simulated wind speed - in production this would come from FDC
        uint256 currentWindSpeed = 750; // 7.5 mph as placeholder

        marketData = MarketData({
            currentWindSpeed: currentWindSpeed,
            flrUsdPrice: flrUsdPrice,
            lastUpdate: block.timestamp
        });

        emit MarketDataUpdated(currentWindSpeed, flrUsdPrice, block.timestamp);
    }

    /**
     * @dev Get user's open positions
     * @param _user User address
     * @return Array of contract IDs
     */
    function getUserPositions(address _user) external view returns (bytes32[] memory) {
        return userPositions[_user];
    }

    /**
     * @dev Get contract details
     * @param _contractId Contract ID
     * @return WindFuture struct
     */
    function getContract(bytes32 _contractId) external view returns (WindFuture memory) {
        return windFutures[_contractId];
    }

    /**
     * @dev Get all active contracts
     * @return Array of contract IDs
     */
    function getAllContracts() external view returns (bytes32[] memory) {
        return allContracts;
    }

    /**
     * @dev Get current market data
     * @return MarketData struct
     */
    function getMarketData() external view returns (MarketData memory) {
        return marketData;
    }

    /**
     * @dev Emergency function to update FTSO interface
     */
    function updateFtsoInterface() external onlyOwner {
        ftsoV2 = contractRegistry.getFtsoV2();
    }
}

contracts/RainfallIndex.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title RainfallIndex
 * @dev Smart contract for trading rainfall-based weather derivatives on Flow EVM
 * @notice This contract enables creation, trading, and settlement of rainfall options
 */
contract RainfallIndex is ReentrancyGuard, Ownable, Pausable {

    // Events
    event OptionCreated(bytes32 indexed optionId, address indexed creator, uint256 strike, uint256 premium, uint256 expiry);
    event OptionPurchased(bytes32 indexed optionId, address indexed buyer, uint256 quantity, uint256 totalCost);
    event OptionSettled(bytes32 indexed optionId, uint256 actualRainfall, uint256 payout);
    event OracleUpdated(address indexed newOracle);
    event RainfallDataUpdated(string indexed stationId, uint256 rainfall, uint256 timestamp);
    event EmergencyWithdrawal(address indexed user, uint256 amount);

    // Structs
    struct RainfallOption {
        bytes32 optionId;
        string stationId;
        uint256 strike;          // Strike price in mm of rainfall
        uint256 premium;         // Premium in wei
        uint256 expiry;          // Expiry timestamp
        uint256 totalSupply;     // Total options available
        uint256 purchased;       // Options purchased
        bool isCall;             // true for call, false for put
        bool settled;            // Settlement status
        uint256 settlementValue; // Final settlement value
        address creator;         // Option creator
    }

    struct Position {
        bytes32 optionId;
        uint256 quantity;
        uint256 entryPrice;
        bool isLong;            // true for buyer, false for seller
    }

    struct RainfallData {
        uint256 value;          // Rainfall in mm (scaled by 1e6 for precision)
        uint256 timestamp;
        bool verified;
        string source;          // Data source (WeatherXM, Chainlink, etc.)
    }

    // State variables
    mapping(bytes32 => RainfallOption) public options;
    mapping(address => mapping(bytes32 => Position)) public positions;
    mapping(string => RainfallData) public rainfallData;
    mapping(address => uint256) public balances;

    bytes32[] public activeOptions;
    string[] public weatherStations;

    address public weatherOracle;
    uint256 public constant PRECISION = 1e6;
    uint256 public constant MIN_PREMIUM = 0.001 ether;
    uint256 public constant MAX_EXPIRY_DURATION = 365 days;
    uint256 public protocolFee = 250; // 2.5% in basis points
    uint256 public totalVolumeTraded;

    // Modifiers
    modifier onlyOracle() {
        require(msg.sender == weatherOracle, "Only oracle can call this function");
        _;
    }

    modifier validOption(bytes32 optionId) {
        require(options[optionId].optionId != bytes32(0), "Option does not exist");
        _;
    }

    modifier notExpired(bytes32 optionId) {
        require(block.timestamp < options[optionId].expiry, "Option has expired");
        _;
    }

    modifier notSettled(bytes32 optionId) {
        require(!options[optionId].settled, "Option already settled");
        _;
    }

    constructor(address _weatherOracle) Ownable(msg.sender) {
        weatherOracle = _weatherOracle;

        // Initialize with Dallas weather station
        weatherStations.push("wxm_dallas_001");
    }

    /**
     * @dev Create a new rainfall option
     * @param stationId Weather station identifier
     * @param strike Strike price in mm of rainfall
     * @param premium Premium per option in wei
     * @param expiry Expiry timestamp
     * @param totalSupply Total number of options to create
     * @param isCall True for call option, false for put option
     */
    function createOption(
        string memory stationId,
        uint256 strike,
        uint256 premium,
        uint256 expiry,
        uint256 totalSupply,
        bool isCall
    ) external payable whenNotPaused nonReentrant returns (bytes32) {
        require(premium >= MIN_PREMIUM, "Premium too low");
        require(expiry > block.timestamp, "Expiry must be in future");
        require(expiry <= block.timestamp + MAX_EXPIRY_DURATION, "Expiry too far in future");
        require(totalSupply > 0, "Total supply must be positive");
        require(strike > 0, "Strike must be positive");

        // Calculate collateral requirement for option writer
        uint256 maxPayout = isCall ? 
            (strike * totalSupply * PRECISION) / PRECISION : // Call: unlimited upside, capped by practical limits
            (strike * totalSupply); // Put: max payout is strike price

        require(msg.value >= maxPayout, "Insufficient collateral");

        bytes32 optionId = keccak256(abi.encodePacked(
            stationId,
            strike,
            expiry,
            isCall,
            block.timestamp,
            msg.sender
        ));

        require(options[optionId].optionId == bytes32(0), "Option already exists");

        options[optionId] = RainfallOption({
            optionId: optionId,
            stationId: stationId,
            strike: strike,
            premium: premium,
            expiry: expiry,
            totalSupply: totalSupply,
            purchased: 0,
            isCall: isCall,
            settled: false,
            settlementValue: 0,
            creator: msg.sender
        });

        activeOptions.push(optionId);

        // Store excess collateral
        balances[msg.sender] += msg.value - maxPayout;

        emit OptionCreated(optionId, msg.sender, strike, premium, expiry);
        return optionId;
    }

    /**
     * @dev Purchase rainfall options
     * @param optionId Option to purchase
     * @param quantity Number of options to buy
     */
    function purchaseOption(bytes32 optionId, uint256 quantity) 
        external 
        payable 
        whenNotPaused 
        nonReentrant 
        validOption(optionId) 
        notExpired(optionId) 
        notSettled(optionId) 
    {
        RainfallOption storage option = options[optionId];
        require(option.purchased + quantity <= option.totalSupply, "Insufficient options available");

        uint256 totalCost = option.premium * quantity;
        uint256 fee = (totalCost * protocolFee) / 10000;
        uint256 totalRequired = totalCost + fee;

        require(msg.value >= totalRequired, "Insufficient payment");

        // Update option state
        option.purchased += quantity;

        // Update position
        Position storage position = positions[msg.sender][optionId];
        if (position.quantity == 0) {
            position.optionId = optionId;
            position.isLong = true;
        }
        position.quantity += quantity;
        position.entryPrice = ((position.entryPrice * (position.quantity - quantity)) + totalCost) / position.quantity;

        // Transfer premium to option creator
        balances[option.creator] += totalCost;

        // Return excess payment
        if (msg.value > totalRequired) {
            balances[msg.sender] += msg.value - totalRequired;
        }

        totalVolumeTraded += totalCost;

        emit OptionPurchased(optionId, msg.sender, quantity, totalCost);
    }

    /**
     * @dev Update rainfall data (oracle only)
     * @param stationId Weather station identifier
     * @param rainfall Rainfall amount in mm (scaled by PRECISION)
     * @param source Data source identifier
     */
    function updateRainfallData(
        string memory stationId,
        uint256 rainfall,
        string memory source
    ) external onlyOracle {
        rainfallData[stationId] = RainfallData({
            value: rainfall,
            timestamp: block.timestamp,
            verified: true,
            source: source
        });

        emit RainfallDataUpdated(stationId, rainfall, block.timestamp);

        // Auto-settle expired options for this station
        _autoSettleExpiredOptions(stationId);
    }

    /**
     * @dev Settle an expired option
     * @param optionId Option to settle
     */
    function settleOption(bytes32 optionId) 
        external 
        validOption(optionId) 
        notSettled(optionId) 
    {
        RainfallOption storage option = options[optionId];
        require(block.timestamp >= option.expiry, "Option not yet expired");

        RainfallData memory data = rainfallData[option.stationId];
        require(data.verified, "No verified rainfall data");
        require(data.timestamp >= option.expiry - 24 hours, "Stale rainfall data");

        uint256 actualRainfall = data.value;
        uint256 payout = _calculatePayout(option, actualRainfall);

        option.settled = true;
        option.settlementValue = payout;

        emit OptionSettled(optionId, actualRainfall, payout);
    }

    /**
     * @dev Claim settlement proceeds
     * @param optionId Settled option
     */
    function claimSettlement(bytes32 optionId) 
        external 
        nonReentrant 
        validOption(optionId) 
    {
        RainfallOption storage option = options[optionId];
        require(option.settled, "Option not settled");

        Position storage position = positions[msg.sender][optionId];
        require(position.quantity > 0, "No position to claim");

        uint256 totalPayout = (option.settlementValue * position.quantity) / option.totalSupply;
        position.quantity = 0; // Prevent re-claiming

        if (totalPayout > 0) {
            balances[msg.sender] += totalPayout;
        }
    }

    /**
     * @dev Withdraw user balance
     * @param amount Amount to withdraw
     */
    function withdraw(uint256 amount) external nonReentrant {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Withdrawal failed");
    }

    /**
     * @dev Calculate option payout based on actual rainfall
     */
    function _calculatePayout(RainfallOption memory option, uint256 actualRainfall) 
        internal 
        pure 
        returns (uint256) 
    {
        if (option.isCall) {
            // Call option: pays when actual > strike
            if (actualRainfall > option.strike * PRECISION) {
                return ((actualRainfall - (option.strike * PRECISION)) * option.purchased) / PRECISION;
            }
        } else {
            // Put option: pays when actual < strike
            if (actualRainfall < option.strike * PRECISION) {
                return (((option.strike * PRECISION) - actualRainfall) * option.purchased) / PRECISION;
            }
        }
        return 0;
    }

    /**
     * @dev Auto-settle expired options for a weather station
     */
    function _autoSettleExpiredOptions(string memory stationId) internal {
        for (uint256 i = 0; i < activeOptions.length; i++) {
            bytes32 optionId = activeOptions[i];
            RainfallOption storage option = options[optionId];

            if (
                !option.settled &&
                block.timestamp >= option.expiry &&
                keccak256(bytes(option.stationId)) == keccak256(bytes(stationId))
            ) {
                RainfallData memory data = rainfallData[stationId];
                uint256 payout = _calculatePayout(option, data.value);

                option.settled = true;
                option.settlementValue = payout;

                emit OptionSettled(optionId, data.value, payout);
            }
        }
    }

    // Admin functions
    function setWeatherOracle(address _newOracle) external onlyOwner {
        weatherOracle = _newOracle;
        emit OracleUpdated(_newOracle);
    }

    function addWeatherStation(string memory stationId) external onlyOwner {
        weatherStations.push(stationId);
    }

    function setProtocolFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 1000, "Fee too high"); // Max 10%
        protocolFee = _newFee;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Emergency function
    function emergencyWithdraw() external {
        require(paused(), "Contract must be paused");
        uint256 balance = balances[msg.sender];
        require(balance > 0, "No balance to withdraw");

        balances[msg.sender] = 0;

        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "Emergency withdrawal failed");

        emit EmergencyWithdrawal(msg.sender, balance);
    }

    // View functions
    function getActiveOptions() external view returns (bytes32[] memory) {
        return activeOptions;
    }

    function getWeatherStations() external view returns (string[] memory) {
        return weatherStations;
    }

    function getOptionDetails(bytes32 optionId) external view returns (RainfallOption memory) {
        return options[optionId];
    }

    function getUserPosition(address user, bytes32 optionId) external view returns (Position memory) {
        return positions[user][optionId];
    }

    function getRainfallData(string memory stationId) external view returns (RainfallData memory) {
        return rainfallData[stationId];
    }

    // Receive function to accept ETH
    receive() external payable {
        balances[msg.sender] += msg.value;
    }
}

